const navItems=document.querySelectorAll(".header__nav-item");navItems.forEach(e=>{e.addEventListener("click",()=>{const t=e.querySelector("ul");t&&t.classList.toggle("active")})});const mobileMenuBtn=document.querySelector(".header__mobile-menu"),headerNav=document.querySelector(".header__nav");mobileMenuBtn.addEventListener("click",()=>{headerNav.classList.toggle("active"),document.body.classList.toggle("lock")});const mobileMenuCloseBtn=document.querySelector(".header__mobile-menu-icon");mobileMenuCloseBtn.addEventListener("click",()=>{headerNav.classList.toggle("active"),document.body.classList.toggle("lock")});